survey %>% select(prod_env, FA) %>% transform(FA = as.numeric(as.character(FA)))%>% ggplot(aes(x= prod_env, y= FA, fill = prod_env)) + geom_boxplot() + theme(panel.background = element_rect(fill = "white"), panel.border = element_rect(color = "grey85", fill = NA), panel.grid.major = element_line(color = "grey85", linetype = "dashed"), axis.title = element_text(size = rel(2)), axis.text = element_text(size = rel(1.5))) + ylab("rice field are (ha)") + xlab("Production environment") + scale_fill_discrete(name = "Production env")
