survey %>% select(prod_env, FA, N, P, K) %>% transform(FA = as.numeric(as.character(FA))) %>% mutate(N_ha = N/FA, P_ha = P/FA, K_ha = K/FA) %>% select(prod_env, N_ha, P_ha, K_ha) %>% melt(var.name = c("N_ha", "P_ha", "K_ha")) %>% ggplot(aes(x = variable, y = value, fill = prod_env)) + geom_boxplot() + ylim(c(0,300)) + scale_fill_discrete( name = "Previous crop") + theme(panel.background = element_rect(fill = "white"), panel.border = element_rect(color = "grey85", fill = NA), panel.grid.major = element_line(color = "grey85", linetype = "dashed"), axis.title = element_text(size = rel(1.5)), axis.text = element_text(size = rel(1.2))) + ylab("amount (kg/ha)") + xlab("Mineral fertilizer")
